import React, { Component } from 'react';
import { Button, Form, Grid, Header, Segment, Icon } from 'semantic-ui-react';
import Cookies from 'js-cookie';
import { Router } from '../routes';
import { Helmet } from 'react-helmet';
import Link from 'next/link';

class VoterLogin extends Component {
	state = {
		email: '',
		password: '',
		loading: false,
		error: ''
	};

	handleChange = (e, { name, value }) => {
		this.setState({ [name]: value });
	};

	handleSubmit = async () => {
		const { email, password } = this.state;
		this.setState({ loading: true, error: '' });

		try {
			const response = await fetch('voter/authenticate', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/x-www-form-urlencoded',
				},
				body: `email=${encodeURIComponent(email)}&password=${encodeURIComponent(password)}`
			});

			const data = await response.json();

			if (data.status === 'success') {
				Cookies.set('voter_email', email);
				Cookies.set('address', data.data.election_address);
				Router.pushRoute(`/election/${data.data.election_address}/vote`);
			} else {
				this.setState({ error: data.message || 'Login failed', loading: false });
			}
		} catch (err) {
			this.setState({ error: 'Network error. Please try again.', loading: false });
		}
	};

	render() {
		const { email, password, loading, error } = this.state;

		return (
			<div style={styles.container}>
				<Helmet>
					<title>Voter Login</title>
					<link rel="shortcut icon" type="image/x-icon" href="../../static/logo3.png" />
					<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				</Helmet>

				<div style={styles.backButton}>
					<Link href="/">
						<Button icon labelPosition='left' basic color='blue'>
							<Icon name='arrow left' />
							Back to Home
						</Button>
					</Link>
				</div>

				<Grid textAlign="center" verticalAlign="middle" style={styles.grid}>
					<Grid.Column style={styles.column}>
						<Header as="h2" style={styles.header}>
							<Icon name='user circle' />
							Voter Login
						</Header>

						<Form size='large' onSubmit={this.handleSubmit}>
							<Segment stacked style={styles.segment}>
								{error && (
									<Message negative style={styles.errorMessage}>
										<Message.Header>Login Error</Message.Header>
										<p>{error}</p>
									</Message>
								)}

								<Form.Input
									fluid
									name="email"
									icon='user'
									iconPosition='left'
									placeholder='Email address'
									value={email}
									onChange={this.handleChange}
									required
								/>

								<Form.Input
									fluid
									name="password"
									icon='lock'
									iconPosition='left'
									placeholder='Password'
									type='password'
									value={password}
									onChange={this.handleChange}
									required
								/>

								<Button
									color='blue'
									fluid
									size='large'
									loading={loading}
									disabled={loading}
									style={styles.submitButton}
								>
									Login
								</Button>
							</Segment>
						</Form>

						<Message style={styles.footerMessage}>
							Need help? <a href='#'>Contact support</a>
						</Message>
					</Grid.Column>
				</Grid>
			</div>
		);
	}
}

const styles = {
	container: {
		position: 'relative',
		minHeight: '100vh',
		background: 'linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url("/static/blockchain.jpg") no-repeat center center',
		backgroundSize: 'cover',
		padding: '20px'
	},
	backButton: {
		position: 'absolute',
		top: '20px',
		left: '20px',
		zIndex: 100
	},
	grid: {
		height: '100%',
		margin: 0
	},
	column: {
		maxWidth: 450,
		backgroundColor: 'rgba(255,255,255,0.9)',
		borderRadius: '8px',
		padding: '30px',
		boxShadow: '0 4px 20px rgba(0,0,0,0.15)'
	},
	header: {
		color: '#2185d0',
		marginBottom: '30px'
	},
	segment: {
		border: 'none',
		boxShadow: 'none',
		padding: '30px'
	},
	submitButton: {
		marginTop: '20px',
		borderRadius: '4px'
	},
	errorMessage: {
		textAlign: 'left',
		marginBottom: '20px'
	},
	footerMessage: {
		marginTop: '20px',
		textAlign: 'center',
		backgroundColor: 'transparent',
		boxShadow: 'none'
	}
};

export default VoterLogin;
