import React from 'react';
import { Menu } from 'semantic-ui-react';
import Cookies from 'js-cookie';

export default props => {
	return (
		<div className="header">
			<style jsx>{`
				.header {
					z-index: 10;
					background: #ffffff;
					border-bottom: 1px solid #e0e0e0;
					padding: 0;
				}
				.menu-container {
					background: transparent !important;
					border: none !important;
					box-shadow: none !important;
					padding: 0;
					margin: 0;
					height: 60px;
				}
				.title-text {
					font-size: 20px;
					font-weight: 600;
					color: #2c3e50;
					padding: 0 0 0 2rem;
					margin: 0;
					display: flex;
					align-items: center;
					height: 100%;
				}
				.user-section {
					display: flex;
					align-items: center;
					gap: 8px;
					padding: 0 2rem 0 0;
					color: #5a6270;
					font-size: 14px;
					font-weight: 500;
				}
				.user-icon {
					color: #7a859e !important;
					margin: 0 !important;
					font-size: 16px !important;
				}
				.divider {
					display: none;
				}
				@media (max-width: 768px) {
					.title-text {
						font-size: 18px;
						padding-left: 1rem;
					}
					.user-section {
						padding-right: 1rem;
						font-size: 13px;
					}
				}
			`}</style>

			<Menu secondary className="menu-container">
				<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />

				<Menu.Item className="title-text">
					Every vote matters
				</Menu.Item>

				<Menu.Menu position="right">
					<Menu.Item className="user-section">
						<Menu.Item
							icon="user outline"
							className="user-icon"
						/>
						{Cookies.get('company_email') || Cookies.get('voter_email')}
					</Menu.Item>
				</Menu.Menu>
			</Menu>

			<hr className="divider" />

			{props.children}
		</div>
	);
};
