import web3 from './web3';
import ElectionFactory from './Build/ElectionFact.json';

let instance;

const initializeContract = async () => {
    try {
        // Client-side only check
        if (typeof window === 'undefined') {
            return {
                methods: {
                    isCompanyRegistered: () => ({ call: () => Promise.resolve(false) }),
                    registerCompany: () => ({ send: () => Promise.resolve() }),
                    getDeployedElection: () => ({ call: () => Promise.resolve() })
                }
            };
        }

        const web3Instance = window.getWeb3 ? await window.getWeb3() : web3;
        const contractABI = ElectionFactory.abi;
        const contractAddress = '0x16795911522115Ef7D4F099f1aBe1F76C2dB6AC8';

        instance = new web3Instance.eth.Contract(contractABI, contractAddress);
        return instance;
    } catch (err) {
        console.error("Contract init error:", err);
        throw err;
    }
};

// Export a promise that resolves to the contract instance
export default initializeContract();
