import React from 'react';
import { Button, Icon, Grid, Header, Segment, Container } from 'semantic-ui-react';
import { Link } from '../routes';

const LandingPage = () => {
	// Data for feature boxes
	const features = [
		{ title: "Secure", description: "Votes cannot be altered or deleted" },
		{ title: "Transparent", description: "Public ledger for full traceability" },
		{ title: "Private", description: "Anonymous voting" },
		{ title: "Fast", description: "Real-time results" }
	];

	return (
		<div style={{ overflowX: 'hidden' }}>
			{/* --- Hero Section --- */}
			<div style={{
				backgroundImage: "url('/static/block.png')",
				backgroundSize: 'cover',
				backgroundPosition: 'center',
				backgroundRepeat: 'no-repeat',
				width: '100vw',
				height: '100vh',
				color: 'white',
				display: 'flex',
				flexDirection: 'column',
				alignItems: 'center',
				justifyContent: 'center',
				textAlign: 'center',
				margin: '0 auto',
			}}>
				{/* ... (keep your existing hero content) ... */}
			</div>

			{/* --- Features Section (Simplified with map) --- */}
			<div style={{
				padding: '4rem 1rem',
				backgroundColor: '#F1EEDC',
				textAlign: 'center',
				margin: '0 auto'
			}}>
				<h2 style={{ fontSize: '2.5rem', marginBottom: '2rem' }}>Key Features</h2>

				<div style={{
					display: 'flex',
					justifyContent: 'space-between',
					gap: '1.5rem',
					flexWrap: 'wrap',
					maxWidth: '1200px',
					margin: '0 auto'
				}}>
					{features.map((feature, index) => (
						<div key={index} style={{
							flex: '1',
							minWidth: '200px',
							backgroundColor: 'white',
							padding: '1.5rem',
							borderRadius: '8px',
							boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
						}}>
							<h3 style={{ fontSize: '1.5rem' }}>{feature.title}</h3>
							<p>{feature.description}</p>
						</div>
					))}
				</div>
			</div>

			{/* --- How It Works Section --- */}
			<Segment style={{ padding: '8em 0em', backgroundColor: '#F1EEDC' }} vertical>
				{/* ... (keep your existing how-it-works content) ... */}
			</Segment>

			{/* --- Footer --- */}
			<Segment inverted vertical style={{ padding: '5em 0em', backgroundColor: '#F1EEDC' }}>
				{/* ... (keep your existing footer) ... */}
			</Segment>
		</div>
	);
};

export default LandingPage;
