};

class VotingList extends Component {
  state = {
    election_address: Cookies.get('address') || SAMPLE_ELECTION.address,
    election_name: SAMPLE_ELECTION.name,
    election_description: SAMPLE_ELECTION.description,
    candidates: SAMPLE_ELECTION.candidateData,
    cand_name: '',
    cand_desc: '',
    buffer: '',
    ipfsHash: null,
    loading: false,
    useSampleData: true // Flag to indicate we're using sample data
  }

  async componentDidMount() {
    // Set cookies with sample data
    Cookies.set('address', SAMPLE_ELECTION.address);

    try {
      // Try to get real data first
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      this.setState({
        election_name: summary[0],
        election_description: summary[1]
      });

      const c = await election.methods.getNumOfCandidates.call();
      if (c == 0) {
        console.log("No candidates found in blockchain, using sample data");
        this.setSampleCandidates();
        return;
      }

      let candidates = [];
      for (let i = 0; i < c; i++) {
        candidates.push(await election.methods.getCandidate(i).call());
      }

      let i = -1;
      const items = candidates.map(candidate => {
        i++;
        return {
          header: candidate[0],
          description: candidate[1],
          image: (
            <Image id={i} src={`https://ipfs.io/ipfs/${candidate[2]}`} style={{ maxWidth: '100%', maxHeight: '190px' }} />
          ),
          extra: (
            <div>
              <Icon name='pie graph' iconPosition='left' />
              {candidate[3].toString()}
            </div>
          )
        };
      });
      this.setState({
        item: items,
        useSampleData: false
      });
    } catch (err) {
      console.log("Using sample data due to error:", err.message);
      this.setSampleCandidates();
    }
  }

  setSampleCandidates = () => {
    let i = -1;
    const items = SAMPLE_ELECTION.candidateData.map(candidate => {
      i++;
      return {
        header: candidate.name,
        description: candidate.position,
        meta: candidate.bio,
        extra: (
          <div>
            <Icon name='pie graph' iconPosition='left' />
            {candidate.votes.toString()} votes
          </div>
        )
      };
    });
    this.setState({ item: items });
  }

  getElectionDetails = () => {
    const {
      election_name,
      election_description
    } = this.state;

    return (
      <div style={{ marginLeft: '45%', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }

  renderTable = () => {
    return (<Card.Group items={this.state.item} />)
  }


  convertToBuffer = async (reader) => {
    const buffer = await Buffer.from(reader.result);
    this.setState({ buffer });
  };

  onSubmit = async (event) => {
    event.preventDefault();

    if (this.state.useSampleData) {
      alert("This is a demonstration. In a real application, the candidate would be added to the blockchain.");
      return;
    }

    this.setState({ loading: true });
    const accounts = await web3.eth.getAccounts();

    try {
      await ipfs.add(this.state.buffer, (err, ipfsHash) => {
        if (err) {
          alert("Error uploading image to IPFS");
          this.setState({ loading: false });
          return;
        }

        this.setState({ ipfsHash: ipfsHash[0].hash });

        const add = Cookies.get('address');
        const election = Election(add);
        console.log("Adding candidate...", this.state.cand_name, this.state.cand_desc, this.state.ipfsHash, document.getElementById('email').value);

        election.methods.addCandidate(
          this.state.cand_name,
          this.state.cand_desc,
          this.state.ipfsHash,
          document.getElementById('email').value
        ).send({
          from: accounts[0]
        }, (error, transactionHash) => {
          if (error) {
            alert("Error adding candidate to blockchain");
            this.setState({ loading: false });
          } else {
            alert("Candidate added successfully!");

            // AJAX script to register candidate in backend
            const email = document.getElementById('email').value;
            var http = new XMLHttpRequest();
            var url = "/candidate/registerCandidate";
            var params = "email=" + email + "&election_name=" + this.state.election_name;
            http.open("POST", url, true);
            http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
            http.onreadystatechange = function () {
              if (http.readyState == 4 && http.status == 200) {
                var responseObj = JSON.parse(http.responseText);
                if (responseObj.status == "success") {
                  alert(responseObj.message);
                } else {
                  alert(responseObj.message);
                }
                this.setState({ loading: false });
              }
            }.bind(this);
            http.send(params);
          }
        });
      })
    } catch (err) {
      alert("Error in file processing.");
      this.setState({ loading: false });
    }
  };

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Link route={`/election/${Cookies.get('address')}/company_dashboard`}>
          <a>
            <Menu.Item style={{ color: 'grey' }}>
              <Icon name='dashboard' />
              Dashboard
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/candidate_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='user outline' />
              Candidate List
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/voting_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='list' />
              Voter List
            </Menu.Item>
          </a>
        </Link>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('company_email');
    Cookies.remove('company_id');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Candidate list!</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>
              {this.getElectionDetails()}
              <br />
              <br />
              <Grid.Column width={14} style={{ minHeight: '630px' }}>
                <Grid.Column style={{ float: 'left', width: '60%' }}>
                  <Header as='h2' color='black'>
                    Candidate List
                    {this.state.useSampleData &&
                      <Header.Subheader style={{ color: 'orange', fontStyle: 'italic' }}>
                        Using sample data for demonstration
                      </Header.Subheader>
                    }
                  </Header>
                  <Container>
                    <table>
                      {this.renderTable()}
                    </table>
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                  <Container style={{ marginLeft: '50px' }}>
                    <Header as='h2' color='black' textAlign='center'>
                      Add Candidate
                    </Header>
                    <Card style={{ width: '100%' }}>
                      <Form.Group size='large' style={{ marginLeft: '15%', marginRight: '15%' }} >
                        <br />
                        <Form.Input
                          fluid
                          label='Name:'
                          placeholder='Enter candidate name'
                          onChange={event => this.setState({ cand_name: event.target.value })}
                          textAlign='center'
                        />
                        <p>Description:</p>
                        <Form.TextArea
                          placeholder='Describe the candidate here...'
                          style={{ width: '100%' }}
                          onChange={event => this.setState({ cand_desc: event.target.value })}
                        />
                        <br />
                        <p>E-mail ID: </p>
                        <Form.Input
                          fluid
                          id="email"
                          placeholder="Enter candidate email"
                        />
                        <br />

                        <br />
                        <Button
                          primary
                          onClick={this.onSubmit}
                          loading={this.state.loading}
                          style={{ marginBottom: '10px' }}
                        >
                          Register Candidate
                        </Button>
                      </Form.Group>
                    </Card>
                  </Container>
                </Grid.Column>
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
