import React, { Component } from 'react';
import { Button, Form, Grid, Header, Segment, Message, Icon } from 'semantic-ui-react';
import { Router } from '../routes';
import web3 from "../Ethereum/web3";
import Election_Factory from "../Ethereum/election_factory";
import Cookies from 'js-cookie';
import { Helmet } from 'react-helmet';

class CompanyLogin extends Component {
  state = {
    activeForm: 'signin', // 'signin' or 'signup'
    email: '',
    loading: false,
    error: null,
    showPassword: false
  };

  togglePasswordVisibility = (e) => {
    e.preventDefault();
    this.setState(prevState => ({
      showPassword: !prevState.showPassword
    }));
  };
  returnBackImage = () => (
    <div className='login-form'>
      <style JSX>{`
        .login-form {
            width:100vw;
            height:100vh;
            position:absolute; 
            background: #e4f1fe;
            z-index: -1;
        }
      `}</style>
    </div>
  )

  signup = event => {
    const email = document.getElementById('signup_email').value;
    const password = document.getElementById('signup_password').value;
    const repeat_password = document.getElementById('signup_repeat_password').value;
    if (password != repeat_password) {
      alert("Passwords do not match");
    }
    else {
      var http = new XMLHttpRequest();
      var url = 'company/register';
      var params = 'email=' + email + '&password=' + password;
      http.open('POST', url, true);
      //Sending the proper header information
      http.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');
      http.onreadystatechange = function () {//Call a function when the state changes.
        if (http.readyState == 4 && http.status == 200) {
          var responseObj = JSON.parse(http.responseText)
          if (responseObj.status == "success") {
            Cookies.set('company_email', encodeURI(responseObj.data.email));
            alert("Added!");
            Router.pushRoute(`/company_login`);
          }
          else {
            alert(responseObj.message);
          }
        }

      }
      http.send(params);
    }
  }
  signin = async event => {
    event.preventDefault(); // Prevent form submission reload

    try {
      const email = document.getElementById('signin_email').value;
      this.setState({ email: document.getElementById('signin_email').value });
      const password = document.getElementById("signin_password").value;

      // ======== ADD THIS CHECK HERE ========
      if (!email || email.trim() === "") {
        alert("Please enter a valid email");
        return;
      }


      var http = new XMLHttpRequest();
      var url = "company/authenticate";
      var params = "email=" + email + "&password=" + password;
      http.open("POST", url, true);
      //Send the proper header information along with the request
      http.setRequestHeader(
        "Content-type",
        "application/x-www-form-urlencoded"
      );
      http.onreadystatechange = function () {
        //Call a function when the state changes.
        if (http.readyState == 4 && http.status == 200) {
          var responseObj = JSON.parse(http.responseText);
          if (responseObj.status == "success") {
            Cookies.set('company_id', encodeURI(responseObj.data.id));
            Cookies.set('company_email', encodeURI(responseObj.data.email));
          }
          else {
            alert(responseObj.message);
          }

        }
      };
      console.log("PARAMS :", params)
      http.send(params);
      try {
        const accounts = await web3.eth.getAccounts();
        console.log("Accounts :", accounts);
        const summary = await Election_Factory.methods.getDeployedElection(this.state.email).call({ from: accounts[0] });
        console.log("************************************************")
        console.log("Summary :", summary);
        if (summary[2] == "Create an election.") {
          console.log("No election found for this company, redirecting to create election page");

          Router.pushRoute(`/election/create_election`);
        }
        else {
          Cookies.set('address', summary[0]);
          console.log("Election found, redirecting to company dashboard");
          Router.pushRoute(`/election/${summary[0]}/company_dashboard`);
        }
      }
      catch (err) {
        console.log("Error fetching election details. Please try again.");
        console.log(err);
      }
    }
    catch (error) {
      console.log("Error in signin: ", error);
    }
  }

  render() {
    const { activeForm, loading, error, showPassword } = this.state;

    return (
      <div style={styles.container}>
        <Helmet>
          <title>Company Login</title>
          <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo3.png" />
        </Helmet>

        {/* Background elements */}
        <div style={styles.background}></div>
        <div style={styles.overlay}></div>

        <Grid textAlign="center" verticalAlign="middle" style={styles.grid}>
          <Grid.Column style={styles.column}>
            <Header as="h2" style={styles.header}>
              Company Portal
            </Header>

            <div style={styles.toggleContainer}>
              <Button.Group fluid>
                <Button
                  primary={activeForm === 'signin'}
                  basic={activeForm !== 'signin'}
                  onClick={() => this.setState({ activeForm: 'signin', error: null })}
                  style={styles.toggleButton}
                >
                  Sign In
                </Button>
                <Button
                  primary={activeForm === 'signup'}
                  basic={activeForm !== 'signup'}
                  onClick={() => this.setState({ activeForm: 'signup', error: null })}
                  style={styles.toggleButton}
                >
                  Sign Up
                </Button>
              </Button.Group>
            </div>

            <Segment stacked style={styles.segment}>
              {error && (
                <Message negative style={styles.message}>
                  {error}
                </Message>
              )}

              {/* Sign In Form - Always visible */}
              <div style={{
                display: 'block',
                opacity: activeForm === 'signin' ? 1 : 0.5,
                transition: 'opacity 0.3s ease',
                marginBottom: activeForm === 'signin' ? '2rem' : '1rem'
              }}>
                <h3 style={{ textAlign: 'center', color: activeForm === 'signin' ? '#273082' : '#999' }}>Sign In</h3>
                <Form size="large" onSubmit={this.signin}>
                  <Form.Input
                    fluid
                    id="signin_email"
                    icon="user"
                    iconPosition="left"
                    placeholder="Email"
                    required
                    style={styles.input}
                    disabled={activeForm !== 'signin'}
                  />
                  <Form.Input
                    fluid
                    id="signin_password"
                    icon={<Icon name='lock' />}
                    iconPosition="left"
                    placeholder="Password"
                    type={showPassword ? 'text' : 'password'}
                    required
                    style={styles.input}
                    disabled={activeForm !== 'signin'}
                    action={activeForm === 'signin' ? {
                      icon: showPassword ? 'eye slash' : 'eye',
                      onClick: this.togglePasswordVisibility,
                      style: styles.eyeAction
                    } : null}
                  />
                  {activeForm === 'signin' && (
                    <Button
                      color="blue"
                      fluid
                      size="large"
                      loading={loading}
                      disabled={loading}
                      style={styles.button}
                    >
                      Sign In
                    </Button>
                  )}
                </Form>
              </div>

              <Divider horizontal style={{ margin: '1.5rem 0' }}>OR</Divider>

              {/* Sign Up Form - Always visible */}
              <div style={{
                display: 'block',
                opacity: activeForm === 'signup' ? 1 : 0.5,
                transition: 'opacity 0.3s ease'
              }}>
                <h3 style={{ textAlign: 'center', color: activeForm === 'signup' ? '#273082' : '#999' }}>Sign Up</h3>
                <Form size="large" onSubmit={this.signup}>
                  <Form.Input
                    fluid
                    id="signup_email"
                    icon="user"
                    iconPosition="left"
                    placeholder="Email"
                    required
                    style={styles.input}
                    disabled={activeForm !== 'signup'}
                  />
                  <Form.Input
                    fluid
                    id="signup_password"
                    icon="lock"
                    iconPosition="left"
                    placeholder="Password"
                    type="password"
                    required
                    style={styles.input}
                    disabled={activeForm !== 'signup'}
                  />
                  <Form.Input
                    fluid
                    id="signup_repeat_password"
                    icon="lock"
                    iconPosition="left"
                    placeholder="Repeat Password"
                    type="password"
                    required
                    style={styles.input}
                    disabled={activeForm !== 'signup'}
                  />
                  {activeForm === 'signup' && (
                    <Button
                      color="blue"
                      fluid
                      size="large"
                      style={styles.button}
                    >
                      Sign Up
                    </Button>
                  )}
                </Form>
              </div>
            </Segment>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
export default DividerExampleVerticalForm;
