import React, { Component } from 'react';
import { Grid, Header, Button, Form, Input, Icon, Menu, Modal, Sidebar, Container, Card, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Cookies from 'js-cookie';
import { Link, Router } from '../../routes';
import Election from '../../Ethereum/election';
import { Helmet } from 'react-helmet';

// Hardcoded sample election data
const SAMPLE_ELECTION = {
  address: '0xa579675518D32c99B6f1929AFe8397d42D896f84',
  name: 'Annual Board Election',
  description: 'Election for Board of Directors 2023',
  adminEmail: 'company@example.com',
  voters: 150,
  candidates: 5,
  totalVotes: 120,
  candidateData: [
    {
      email: 'john.doe@company.com',
      name: 'John Doe',
      position: 'CEO',
      votes: 45,
      bio: 'Experienced leader with 10+ years in industry',
      imageHash: 'QmWvP2y5IqW7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z'
    },
    {
      email: 'jane.smith@company.com',
      name: 'Jane Smith',
      position: 'CTO',
      votes: 38,
      bio: 'Technology innovator and strategic thinker',
      imageHash: 'QmWvP2y5IqW7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z'
    },
    {
      email: 'robert.johnson@company.com',
      name: 'Robert Johnson',
      position: 'CFO',
      votes: 22,
      bio: 'Financial expert with proven track record',
      imageHash: 'QmWvP2y5IqW7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z'
    },
    {
      email: 'sarah.williams@company.com',
      name: 'Sarah Williams',
      position: 'COO',
      votes: 10,
      bio: 'Operations specialist focused on efficiency',
      imageHash: 'QmWvP2y5IqW7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z'
    },
    {
      email: 'michael.brown@company.com',
      name: 'Michael Brown',
      position: 'CMO',
      votes: 5,
      bio: 'Marketing guru with creative vision',
      imageHash: 'QmWvP2y5IqW7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z7W7Z'
    }
  ],
  voterData: [
    { id: 1, email: 'voter1@company.com', hasVoted: true },
    { id: 2, email: 'voter2@company.com', hasVoted: true },
    { id: 3, email: 'voter3@company.com', hasVoted: false },
    { id: 4, email: 'voter4@company.com', hasVoted: true },
    { id: 5, email: 'voter5@company.com', hasVoted: false },
  ],
  electionStatus: 'active',
  startDate: '2023-10-01',
  endDate: '2023-10-15'
};

class VotingList extends Component {
  state = {
    election_address: Cookies.get('address') || SAMPLE_ELECTION.address,
    election_name: SAMPLE_ELECTION.name,
    election_description: SAMPLE_ELECTION.description,
    emailArr: [],
    idArr: [],
    item: [],
    useSampleData: true,
    nextVoterId: 6 // Starting ID for new voters
  }

  async componentDidMount() {
    // Set cookies with sample data
    Cookies.set('address', SAMPLE_ELECTION.address);

    try {
      // Try to get real data first
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      this.setState({
        election_name: summary[0],
        election_description: summary[1],
        useSampleData: false
      });

      // Try to get real voter data
      var http = new XMLHttpRequest();
      var url = '/voter/';
      var params = 'election_address=' + this.state.election_address;
      http.open("POST", url, true);
      let email = [];
      let id = []

      http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
      http.onreadystatechange = function () {
        if (http.readyState == 4 && http.status == 200) {
          var responseObj = JSON.parse(http.responseText);
          if (responseObj.status == "success") {
            for (let voter of responseObj.data.voters) {
              email.push(voter.email);
              id.push(voter.id);
            }
            this.setState({
              emailArr: email,
              idArr: id
            });
            this.createVoterItems(email, id);
          }
        }
      }.bind(this);
      http.send(params);

    } catch (err) {
      console.log("Using sample data due to error:", err.message);
      // Use sample data if real data is not available
      this.setSampleVoterData();
    }
  }

  setSampleVoterData = () => {
    const email = SAMPLE_ELECTION.voterData.map(voter => voter.email);
    const id = SAMPLE_ELECTION.voterData.map(voter => voter.id);

    this.setState({
      emailArr: email,
      idArr: id,
      useSampleData: true
    });
    this.createVoterItems(email, id);
  }

  createVoterItems = (emailArr, idArr) => {
    let i = -1;
    const items = idArr.map(id => {
      i++;
      return {
        header: emailArr[i],
        description: (
          <div>
            <br />
            <Modal size={"tiny"} trigger={
              <Button basic id={id} color="green">
                Edit
              </Button>
            } closeIcon
            >
              <Modal.Header>Edit E-mail ID</Modal.Header>
              <center>
                <Modal.Content>
                  <Input
                    id={`EmailVal${id}`}
                    placeholder='E-mail ID'
                    defaultValue={emailArr[i]}
                    style={{ marginBottom: '5%', marginTop: '5%' }}
                  />
                </Modal.Content>
                <Modal.Actions>
                  <Button
                    positive
                    icon="checkmark"
                    labelPosition="right"
                    content="Yes"
                    padding="30"
                    style={{ marginBottom: "10px" }}
                    onClick={this.updateEmail}
                    id={id}
                  />
                  <Button negative>No</Button>
                </Modal.Actions>
              </center>
            </Modal>
            <Button
              negative
              basic
              id={id}
              value={id}
              onClick={this.deleteEmail}
              style={{ marginLeft: '10px' }}
            >
              Delete
            </Button>
          </div>
        )
      };
    });
    this.setState({ item: items });
  }

  updateEmail = event => {
    if (this.state.useSampleData) {
      const voterId = event.currentTarget.id;
      const newEmail = document.getElementById(`EmailVal${voterId}`).value;

      // Update the email in the state
      const updatedEmailArr = [...this.state.emailArr];
      const voterIndex = this.state.idArr.indexOf(parseInt(voterId));

      if (voterIndex !== -1) {
        updatedEmailArr[voterIndex] = newEmail;
        this.setState({ emailArr: updatedEmailArr });
        this.createVoterItems(updatedEmailArr, this.state.idArr);
        alert(`Voter email updated to: ${newEmail}`);
      }
      return;
    }

    const d = event.currentTarget.id;
    const st = 'EmailVal' + event.currentTarget.id;
    const a = document.getElementById(st).value;
    const b = this.state.election_name;
    const c = this.state.election_description;

    var http = new XMLHttpRequest();
    var url = '/voter/' + d;
    var params = 'email=' + a + '&election_name=' + b + '&election_description=' + c;
    http.open("PUT", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    http.onreadystatechange = function () {
      if (http.readyState == 4 && http.status == 200) {
        var responseObj = JSON.parse(http.responseText);
        if (responseObj.status == "success") {
          alert(responseObj.message);
        }
      }
    };
    http.send(params);
  }

  deleteEmail = event => {
    if (this.state.useSampleData) {
      const voterId = parseInt(event.currentTarget.value);

      // Remove the voter from the state
      const voterIndex = this.state.idArr.indexOf(voterId);

      if (voterIndex !== -1) {
        const updatedEmailArr = [...this.state.emailArr];
        const updatedIdArr = [...this.state.idArr];

        updatedEmailArr.splice(voterIndex, 1);
        updatedIdArr.splice(voterIndex, 1);

        this.setState({
          emailArr: updatedEmailArr,
          idArr: updatedIdArr
        });
        this.createVoterItems(updatedEmailArr, updatedIdArr);
        alert(`Voter with ID ${voterId} has been removed`);
      }
      return;
    }

    var http = new XMLHttpRequest();
    var url = '/voter/' + event.currentTarget.value;
    http.open("DELETE", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    http.onreadystatechange = function () {
      if (http.readyState == 4 && http.status == 200) {
        var responseObj = JSON.parse(http.responseText);
        if (responseObj.status == "success") {
          alert(responseObj.message);
        }
      }
    };
    http.send();
  }

  register = event => {
    const email = document.getElementById('register_voter_email').value;

    if (!email || !email.includes('@')) {
      alert("Please enter a valid email address");
      return;
    }

    if (this.state.useSampleData) {
      // Add new voter to sample data
      const newVoterId = this.state.nextVoterId;
      const updatedEmailArr = [...this.state.emailArr, email];
      const updatedIdArr = [...this.state.idArr, newVoterId];

      this.setState({
        emailArr: updatedEmailArr,
        idArr: updatedIdArr,
        nextVoterId: newVoterId + 1
      });

      this.createVoterItems(updatedEmailArr, updatedIdArr);
      alert(`Voter ${email} has been added with ID ${newVoterId}`);

      // Clear the input field
      document.getElementById('register_voter_email').value = '';
      return;
    }

    var http = new XMLHttpRequest();
    var url = "/voter/register";
    var params = "email=" + email + "&election_address=" + this.state.election_address + "&election_name=" + this.state.election_name + "&election_description=" + this.state.election_description;
    http.open("POST", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    http.onreadystatechange = function () {
      if (http.readyState == 4 && http.status == 200) {
        var responseObj = JSON.parse(http.responseText);
        if (responseObj.status == "success") {
          alert(responseObj.message);
        }
        else {
          alert(responseObj.message);
        }
      }
    };
    http.send(params);
  }

  renderTable = () => {
    if (this.state.item.length === 0) {
      return (
        <Message info>
          <Icon name="info circle" />
          No voters registered yet. Add voters using the form on the right.
        </Message>
      );
    }

    return (
      <div>
        <Card.Group items={this.state.item} />
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <p>Total Voters: {this.state.emailArr.length}</p>
        </div>
      </div>
    );
  }

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Link route={`/election/${Cookies.get('address')}/company_dashboard`}>
          <a>
            <Menu.Item style={{ color: 'grey', fontColor: 'grey' }}>
              <Icon name='dashboard' />
              Dashboard
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/candidate_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='user outline' />
              Candidate List
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/voting_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='list' />
              Voter List
            </Menu.Item>
          </a>
        </Link>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('company_email');
    Cookies.remove('company_id');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  getElectionDetails = () => {
    const { election_name, election_description } = this.state;

    return (
      <div style={{ marginLeft: '45%', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Voting list</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>
              {this.getElectionDetails()}
              <br />
              <br />
              <Grid.Column width={14} style={{ minHeight: '630px' }}>
                {this.state.useSampleData && (
                  <Message info>
                    <Icon name="info circle" />
                    This is a demonstration using sample voter data. You can add, edit, and remove voters.
                  </Message>
                )}
                <Grid.Column style={{ float: 'left', width: '60%' }}>
                  <Header as='h2' color='black'>
                    Voter List
                  </Header>
                  <Container>
                    {this.renderTable()}
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                  <Container style={{}}>
                    <Header as='h2' color='black'>
                      Register Voter
                    </Header>
                    <Card style={{ width: '100%' }}>
                      <br />
                      <Form.Group size='large' style={{ marginLeft: '15%', marginRight: '15%' }} >
                        <Form.Input
                          style={{ marginTop: '10px' }}
                          fluid
                          id='register_voter_email'
                          label='Email:'
                          placeholder='Enter voter email'
                          textAlign='center'
                        />

                        <br /><br />
                        <Button
                          primary
                          style={{ Bottom: '10px', marginBottom: '15px' }}
                          onClick={this.register}
                        >
                          Register Voter
                        </Button>
                      </Form.Group>
                    </Card>
                  </Container>
                </Grid.Column>
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
