import React, { Component } from 'react';
import { Grid, Button, Header, Icon, Image, Menu, Sidebar, Container, Card } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import { Router } from '../../routes';
import { Helmet } from 'react-helmet';

class VotingList extends Component {
  state = {
    numCand: '3',
    election_address: Cookies.get('address'),
    election_name: 'Company Board Election 2024',
    election_description: 'Annual election for board members and directors',
    candidates: [],
    cand_name: '',
    cand_desc: '',
    buffer: '',
    ipfsHash: null,
    loading: false,
    item: [
      {
        header: "John Anderson",
        description: "Senior Executive with 10+ years experience",
        // image: (
        //   <Image src={`https://ipfs.io/ipfs/QmXoypizjW3WknFiJnKLwHCnL72vedxjQkDDP1mXWo6uco`} style={{ maxWidth: '100%', maxHeight: '190px' }} />
        // ),
        extra: (
          <div>
            <Icon name='pie graph' size='big' iconPosition='left' />
            18
            <Button id="0" style={{ float: 'right' }} onClick={this.vote} primary>Vote!</Button>
          </div>
        )
      },
      {
        header: "Sarah Mitchell",
        description: "Marketing Director and team leader",
        // image: (
        //   <Image src={`https://ipfs.io/ipfs/QmXoypizjW3WknFiJnKLwHCnL72vedxjQkDDP1mXWo6uco`} style={{ maxWidth: '100%', maxHeight: '190px' }} />
        // ),
        extra: (
          <div>
            <Icon name='pie graph' size='big' iconPosition='left' />
            12
            <Button id="1" style={{ float: 'right' }} onClick={this.vote} primary>Vote!</Button>
          </div>
        )
      },
      {
        header: "David Kim",
        description: "Finance expert and budget specialist",
        // image: (
        //   <Image src={`https://ipfs.io/ipfs/QmXoypizjW3WknFiJnKLwHCnL72vedxjQkDDP1mXWo6uco`} style={{ maxWidth: '100%', maxHeight: '190px' }} />
        // ),
        extra: (
          <div>
            <Icon name='pie graph' size='big' iconPosition='left' />
            8
            <Button id="2" style={{ float: 'right' }} onClick={this.vote} primary>Vote!</Button>
          </div>
        )
      }
    ]
  };

  GridExampleGrid = () => <Grid>{columns}</Grid>
  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut() {
    Cookies.remove('address');
    Cookies.remove('voter_email');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  async componentDidMount() {
    console.log('Using hardcoded voting data');
  }

  getElectionDetails = () => {
    const {
      election_name,
      election_description
    } = this.state;

    return (
      <div style={{ marginLeft: '45%', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }

  renderTable = () => {
    return (<Card.Group items={this.state.item} />)
  }

  vote = async event => {
    const e = parseInt(event.currentTarget.id, 10);
    alert(`Voted for candidate ${e + 1}!`);
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Vote</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Layout>
              {this.getElectionDetails()}
              <Grid.Column style={{ minHeight: '77vh', marginLeft: '10%' }}>
                <Container>
                  {this.renderTable()}
                </Container>
              </Grid.Column>
            </Layout>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
