import React, { Component } from 'react';
import { Grid, Button, Header, Icon, Menu, Sidebar, Container, Table, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Cookies from 'js-cookie';
import { Router } from '../../routes';
import { Helmet } from 'react-helmet';
import {
  getSharedElectionData,
  subscribeToUpdates
} from '../../components/sharedElectionData ';

class CandidateList extends Component {
  state = {
    election_address: Cookies.get('address'),
    election_name: '',
    election_description: '',
    candidates: [],
    loading: false,
    error: null
  };

  componentDidMount() {
    this.initializeElection();
    // Subscribe to data updates
    this.unsubscribe = subscribeToUpdates(this.handleDataUpdate);
  }

  componentWillUnmount() {
    // Clean up subscription
    if (this.unsubscribe) {
      this.unsubscribe();
    }
  }

  handleDataUpdate = (updatedData) => {
    this.updateUIWithElectionData(updatedData);
  }

  initializeElection = () => {
    try {
      // Use the shared election data
      const electionData = getSharedElectionData();

      this.updateUIWithElectionData(electionData);
    } catch (err) {
      console.error("Error in initializeElection:", err.message);
      this.setState({
        error: err.message,
        loading: false
      });
    }
  }

  updateUIWithElectionData = (electionData) => {
    this.setState({
      electionData,
      election_name: electionData.name,
      election_description: electionData.description,
      candidates: electionData.candidateData,
      loading: false,
      error: null
    });
  }

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('company_email');
    Cookies.remove('company_id');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  getElectionDetails = () => {
    const { election_name, election_description, electionData } = this.state;

    return (
      <div style={{ textAlign: 'center', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
            {electionData && (
              <Header.Subheader>
                Total Candidates: {electionData.candidates} | Total Votes: {electionData.totalVotes}
              </Header.Subheader>
            )}
          </Header.Content>
        </Header>
      </div>
    );
  }

  renderTable = () => {
    const { candidates, loading, error } = this.state;

    if (error) {
      return (
        <Message negative>
          <Message.Header>Error</Message.Header>
          <p>{error}</p>
          <Button primary onClick={this.initializeElection}>Retry</Button>
        </Message>
      );
    }

    if (loading) {
      return (
        <Message info>
          <Icon name="circle notched" loading />
          Loading candidates...
        </Message>
      );
    }

    if (candidates.length === 0) {
      return (
        <Message warning>
          <Icon name="warning circle" />
          No candidates available for this election.
        </Message>
      );
    }

    return (
      <Table celled>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Name</Table.HeaderCell>
            <Table.HeaderCell>Position</Table.HeaderCell>
            <Table.HeaderCell>Email</Table.HeaderCell>
            <Table.HeaderCell>Bio</Table.HeaderCell>
            <Table.HeaderCell>Votes</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          {candidates.map((candidate, index) => (
            <Table.Row key={index}>
              <Table.Cell>{candidate.name}</Table.Cell>
              <Table.Cell>{candidate.position}</Table.Cell>
              <Table.Cell>{candidate.email}</Table.Cell>
              <Table.Cell>{candidate.bio}</Table.Cell>
              <Table.Cell>{candidate.votes}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    );
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Candidates</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Grid.Column width={14}>
              <Layout>
                {this.getElectionDetails()}
                <Container>
                  {this.renderTable()}
                </Container>
              </Layout>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default CandidateList;
