import React, { Component } from 'react';
import { Grid, Header, Button, Form, Input, Icon, Menu, Modal, Sidebar, Container, Card, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import Cookies from 'js-cookie';
import { Link, Router } from '../../routes';
import Election from '../../Ethereum/election';
import { Helmet } from 'react-helmet';

class VotingList extends Component {
  state = {
    election_address: Cookies.get('address'),
    election_name: '',
    election_description: '',
    emailArr: [],
    idArr: [],
    item: [],
    emailError: '',
    editingVoterId: null,
    loading: false,
    refreshTrigger: 0 // Add refresh trigger to force re-renders
  }

  async componentDidMount() {
    await this.loadVoterData();

    try {
      const add = Cookies.get('address');
      const election = Election(add);
      const summary = await election.methods.getElectionDetails().call();
      this.setState({
        election_name: summary[0],
        election_description: summary[1]
      });
    } catch (err) {
      console.log("Error loading election details:", err.message);
    }
  }

  loadVoterData = () => {
    return new Promise((resolve) => {
      var http = new XMLHttpRequest();
      var url = '/voter/';
      var params = 'election_address=' + this.state.election_address;
      http.open("POST", url, true);

      http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
      http.onreadystatechange = () => {
        if (http.readyState === 4 && http.status === 200) {
          try {
            var responseObj = JSON.parse(http.responseText);
            console.log("Voter data response:", responseObj);

            if (responseObj.status === "success" && responseObj.data && responseObj.data.voters) {
              const email = [];
              const id = [];

              for (let voter of responseObj.data.voters) {
                email.push(voter.email);
                id.push(voter.id);
              }

              this.setState({
                emailArr: email,
                idArr: id
              }, () => {
                this.createVoterItems(email, id);
                resolve();
              });
            } else {
              console.log("No voters found or invalid response format");
              this.setState({ emailArr: [], idArr: [], item: [] });
              resolve();
            }
          } catch (e) {
            console.error("Error parsing voter data:", e);
            this.setState({ emailArr: [], idArr: [], item: [] });
            resolve();
          }
        } else if (http.readyState === 4) {
          console.error("HTTP error:", http.status);
          this.setState({ emailArr: [], idArr: [], item: [] });
          resolve();
        }
      };

      http.onerror = () => {
        console.error("Network error loading voter data");
        this.setState({ emailArr: [], idArr: [], item: [] });
        resolve();
      };

      http.send(params);
    });
  }

  createVoterItems = (emailArr, idArr) => {
    console.log("Creating voter items:", emailArr, idArr);

    if (emailArr.length === 0 || idArr.length === 0) {
      this.setState({ item: [] });
      return;
    }

    const items = idArr.map((id, index) => {
      return {
        key: id, // Add unique key
        header: emailArr[index],
        description: (
          <div>
            <br />
            <Modal
              size={"tiny"}
              trigger={
                <Button basic id={id} color="green">
                  Edit
                </Button>
              }
              closeIcon
              onOpen={() => this.handleModalOpen(id, emailArr[index])}
            >
              <Modal.Header>Edit E-mail ID</Modal.Header>
              <center>
                <Modal.Content>
                  <Input
                    id={`EmailVal${id}`}
                    placeholder='E-mail ID'
                    defaultValue={emailArr[index]}
                    style={{ marginBottom: '5%', marginTop: '5%' }}
                    onChange={() => this.validateEmail(id)}
                  />
                  {this.state.emailError && this.state.editingVoterId === id && (
                    <Message negative size='tiny' style={{ marginTop: '10px' }}>
                      {this.state.emailError}
                    </Message>
                  )}
                </Modal.Content>
                <Modal.Actions>
                  <Button
                    positive
                    icon="checkmark"
                    labelPosition="right"
                    content="Yes"
                    padding="30"
                    style={{ marginBottom: "10px" }}
                    onClick={this.updateEmail}
                    id={id}
                    disabled={!!this.state.emailError && this.state.editingVoterId === id}
                  />
                  <Button negative>No</Button>
                </Modal.Actions>
              </center>
            </Modal>
            <Button
              negative
              basic
              id={id}
              value={id}
              onClick={this.deleteEmail}
              style={{ marginLeft: '10px' }}
            >
              Delete
            </Button>
          </div>
        )
      };
    });

    this.setState({ item: items });
  }

  validateEmail = (voterId) => {
    const newEmail = document.getElementById(`EmailVal${voterId}`).value;
    const currentEmails = this.state.emailArr.filter((email, index) =>
      this.state.idArr[index] !== parseInt(voterId)
    );

    if (currentEmails.includes(newEmail)) {
      this.setState({
        emailError: 'This email is already registered to another voter.',
        editingVoterId: voterId
      });
      return false;
    }

    if (!newEmail.includes('@')) {
      this.setState({
        emailError: 'Please enter a valid email address.',
        editingVoterId: voterId
      });
      return false;
    }

    this.setState({
      emailError: '',
      editingVoterId: voterId
    });
    return true;
  }

  handleModalOpen = (voterId, currentEmail) => {
    this.setState({
      emailError: '',
      editingVoterId: voterId
    });

    setTimeout(() => {
      const inputField = document.getElementById(`EmailVal${voterId}`);
      if (inputField) {
        inputField.value = currentEmail;
      }
    }, 100);
  }

  updateEmail = event => {
    const voterId = event.currentTarget.id;

    if (!this.validateEmail(voterId)) {
      return;
    }

    this.setState({ loading: true });

    const st = 'EmailVal' + event.currentTarget.id;
    const a = document.getElementById(st).value;
    const b = this.state.election_name;
    const c = this.state.election_description;

    var http = new XMLHttpRequest();
    var url = '/voter/' + voterId;
    var params = 'email=' + a + '&election_name=' + b + '&election_description=' + c;
    http.open("PUT", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    http.onreadystatechange = () => {
      if (http.readyState === 4) {
        if (http.status === 200) {
          try {
            var responseObj = JSON.parse(http.responseText);
            if (responseObj.status === "success") {
              alert(responseObj.message);
              // Force complete reload of data
              this.loadVoterData().then(() => {
                this.setState({ loading: false, refreshTrigger: this.state.refreshTrigger + 1 });
              });
            } else {
              alert(responseObj.message);
              this.setState({ loading: false });
            }
          } catch (e) {
            alert("Error parsing response");
            this.setState({ loading: false });
          }
        } else {
          alert("Error updating voter email");
          this.setState({ loading: false });
        }
      }
    };
    http.send(params);
  }

  deleteEmail = event => {
    this.setState({ loading: true });

    var http = new XMLHttpRequest();
    var url = '/voter/' + event.currentTarget.value;
    http.open("DELETE", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    http.onreadystatechange = () => {
      if (http.readyState === 4) {
        if (http.status === 200) {
          try {
            var responseObj = JSON.parse(http.responseText);
            if (responseObj.status === "success") {
              alert(responseObj.message);
              // Force complete reload
              this.loadVoterData().then(() => {
                this.setState({ loading: false, refreshTrigger: this.state.refreshTrigger + 1 });
              });
            } else {
              alert(responseObj.message);
              this.setState({ loading: false });
            }
          } catch (e) {
            alert("Error parsing response");
            this.setState({ loading: false });
          }
        } else {
          alert("Error deleting voter");
          this.setState({ loading: false });
        }
      }
    };
    http.send();
  }

  renderTable = () => {
    if (this.state.item.length === 0) {
      return (
        <Message info>
          <Icon name="info circle" />
          No voters registered yet. Add voters using the form on the right.
        </Message>
      );
    }

    return (
      <div>
        <Card.Group items={this.state.item} />
        <div style={{ marginTop: '20px', textAlign: 'center' }}>
          <p>Total Voters: {this.state.emailArr.length}</p>
        </div>
      </div>
    );
  }

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Link route={`/election/${Cookies.get('address')}/company_dashboard`}>
          <a>
            <Menu.Item style={{ color: 'grey', fontColor: 'grey' }}>
              <Icon name='dashboard' />
              Dashboard
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/candidate_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='user outline' />
              Candidate List
            </Menu.Item>
          </a>
        </Link>
        <Link route={`/election/${Cookies.get('address')}/voting_list`}>
          <a>
            <Menu.Item as='a' style={{ color: 'grey' }}>
              <Icon name='list' />
              Voter List
            </Menu.Item>
          </a>
        </Link>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('company_email');
    Cookies.remove('company_id');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  register = event => {
    const emailInput = document.getElementById('register_voter_email');
    const email = emailInput.value.trim();

    if (!email || !email.includes('@')) {
      alert("Please enter a valid email address");
      return;
    }

    if (this.state.emailArr.includes(email)) {
      alert("This email is already registered to another voter.");
      return;
    }

    this.setState({ loading: true });

    var http = new XMLHttpRequest();
    var url = "/voter/register";
    var params = "email=" + encodeURIComponent(email) +
      "&election_address=" + encodeURIComponent(this.state.election_address) +
      "&election_name=" + encodeURIComponent(this.state.election_name) +
      "&election_description=" + encodeURIComponent(this.state.election_description);

    http.open("POST", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

    http.onreadystatechange = () => {
      if (http.readyState === 4) {
        if (http.status === 200) {
          try {
            const responseObj = JSON.parse(http.responseText);
            if (responseObj.status === "success") {
              // Clear input immediately
              emailInput.value = '';

              // Show success message
              alert(responseObj.message);

              // Force complete reload from server to get actual ID
              this.loadVoterData().then(() => {
                this.setState({
                  loading: false,
                  refreshTrigger: this.state.refreshTrigger + 1 // Force re-render
                });
              });
            } else {
              alert(responseObj.message || "Registration failed");
              this.setState({ loading: false });
            }
          } catch (e) {
            alert("Error parsing server response");
            this.setState({ loading: false });
          }
        } else {
          alert("Server error. Please try again.");
          this.setState({ loading: false });
        }
      }
    };

    http.onerror = () => {
      alert("Network error. Please check your connection.");
      this.setState({ loading: false });
    };

    http.send(params);
  }

  getElectionDetails = () => {
    const { election_name, election_description } = this.state;

    return (
      <div style={{ marginLeft: '45%', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Voting list</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Grid.Column width={14}>
              <Layout>
                {this.getElectionDetails()}
                <br />
                <br />
                <div style={{ minHeight: '630px' }}>
                  {this.state.loading && (
                    <Message info>
                      <Icon name="circle notched" loading />
                      Loading...
                    </Message>
                  )}
                  <div style={{ float: 'left', width: '60%' }}>
                    <Header as='h2' color='black'>
                      Voter List
                    </Header>
                    <Container>
                      {this.renderTable()}
                    </Container>
                  </div>
                  <div style={{ float: 'right', width: '30%' }}>
                    <Container>
                      <Header as='h2' color='black'>
                        Register Voter
                      </Header>
                      <Card style={{ width: '100%' }}>
                        <br />
                        <Form style={{ padding: '0 15%' }}>
                          <Form.Field>
                            <label>Email:</label>
                            <Input
                              fluid
                              id='register_voter_email'
                              placeholder='Enter voter email'
                              style={{ marginTop: '10px' }}
                            />
                          </Form.Field>
                          <br />
                          <Button
                            primary
                            fluid
                            onClick={this.register}
                            loading={this.state.loading}
                            style={{ marginBottom: '15px' }}
                          >
                            Register
                          </Button>
                        </Form>
                      </Card>
                    </Container>
                  </div>
                </div>
              </Layout>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
