import React, { Component } from 'react';
import { Grid, Button, Header, Icon, Image, Menu, Sidebar, Container, Card, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import { Router } from '../../routes';
import { Helmet } from 'react-helmet';

class VotingList extends Component {
  state = {
    election_address: Cookies.get('address'),
    election_name: '',
    election_description: '',
    candidates: [],
    item: [],
    loading: true,
    error: null
  };

  componentDidMount() {
    this.initializeElection();
  }

  initializeElection = async () => {
    try {
      const add = Cookies.get('address');
      console.log("Election Address from Cookie:", add);

      if (!add) {
        throw new Error('Election address not found in cookies');
      }

      // IMPORTANT: Check if Election.interface is valid JSON
      let election;
      try {
        election = Election(add);
        console.log("Election instance created successfully");
      } catch (err) {
        console.error("Error creating election instance:", err);
        throw new Error('Failed to initialize election contract. Interface may be invalid.');
      }

      const summary = await election.methods.getElectionDetails().call();
      this.setState({
        election_name: summary[0],
        election_description: summary[1]
      });

      // FIX: Added parentheses to call the method properly
      const c = await election.methods.getNumOfCandidates().call();
      console.log("Number of candidates:", c);

      let candidates = [];
      for (let i = 0; i < c; i++) {
        const candidate = await election.methods.getCandidate(i).call();
        candidates.push(candidate);
      }

      let i = -1;
      const items = candidates.map(candidate => {
        i++;
        return {
          header: candidate[0],
          description: candidate[1],
          image: (
            <Image
              id={i}
              src={`https://ipfs.io/ipfs/${candidate[2]}`}
              style={{ maxWidth: '100%', maxHeight: '190px' }}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found';
              }}
            />
          ),
          extra: (
            <div>
              <Icon name='pie graph' size='big' />
              {candidate[3].toString()} Votes
              <Button id={i} style={{ float: 'right' }} onClick={this.vote} primary>Vote!</Button>
            </div>
          )
        };
      });

      this.setState({
        item: items,
        candidates,
        loading: false,
        error: null
      });
    } catch (err) {
      console.error("Error in initializeElection:", err.message);
      this.setState({
        error: err.message,
        loading: false
      });

      if (err.message.includes('address not found') || err.message.includes('session')) {
        alert("Session expired. Redirecting you to login page...");
        Router.pushRoute('/voter_login');
      }
    }
  }

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('voter_email');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  getElectionDetails = () => {
    const { election_name, election_description } = this.state;

    return (
      <div style={{ textAlign: 'center', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
          </Header.Content>
        </Header>
      </div>
    );
  }

  renderTable = () => {
    const { item, loading, error } = this.state;

    if (error) {
      return (
        <Message negative>
          <Message.Header>Error</Message.Header>
          <p>{error}</p>
          <Button primary onClick={this.initializeElection}>Retry</Button>
        </Message>
      );
    }

    if (loading) {
      return (
        <Message info>
          <Icon name="circle notched" loading />
          Loading candidates...
        </Message>
      );
    }

    if (item.length === 0) {
      return (
        <Message warning>
          <Icon name="warning circle" />
          No candidates available for this election.
        </Message>
      );
    }

    return <Card.Group items={item} />;
  }

  vote = async event => {
    try {
      const e = parseInt(event.currentTarget.id, 10);
      const accounts = await web3.eth.getAccounts();
      const add = Cookies.get('address');
      const voterEmail = Cookies.get('voter_email');

      if (!add || !voterEmail) {
        throw new Error('Session expired. Please login again.');
      }

      const election = Election(add);
      await election.methods.vote(e, voterEmail).send({ from: accounts[0] });
      alert("Voted successfully!");

      // Refresh the candidate list to update vote counts
      this.setState({ loading: true });
      await this.initializeElection();
    } catch (err) {
      console.error("Error voting:", err.message);
      alert(`Voting failed: ${err.message}`);
    }
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Vote</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Grid.Column width={14}>
              <Layout>
                {this.getElectionDetails()}
                <Container>
                  {this.renderTable()}
                </Container>
              </Layout>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
