import React, { Component } from 'react';
import { Button, Form, Grid, Header, Segment, Icon, Message } from 'semantic-ui-react';
import web3 from '../../Ethereum/web3';
import Election_Factory from '../../Ethereum/election_factory';
import { Router } from '../../routes';
import Cookies from 'js-cookie';
import { Helmet } from 'react-helmet';

class CreateElection extends Component {
	state = {
		election_name: '',
		election_description: '',
		loading: false,
		errorMess: '',
		successMessage: ''
	};

	createElection = async (event) => {
		event.preventDefault();
		this.setState({ loading: true, errorMess: '', successMessage: '' });

		try {
			const email = Cookies.get('company_email');
			const accounts = await web3.eth.getAccounts();

			if (!accounts || accounts.length === 0) {
				throw new Error("No Ethereum accounts found. Please make sure MetaMask is connected.");
			}

			if (!email) {
				throw new Error("Company email not found. Please login again.");
			}

			console.log("Creating election with:", {
				from: accounts[0],
				email: email,
				name: this.state.election_name,
				description: this.state.election_description
			});

			// Call the createElection method
			const transaction = await Election_Factory.methods
				.createElection(
					this.state.election_name,
					this.state.election_description
				)
				.send({
					from: accounts[0],
					gas: 500000 // Increased gas limit
				});

			console.log("Transaction successful:", transaction);

			// Get the newly created election address
			// This depends on how your contract emits events or returns data
			let electionAddress = null;

			// Method 1: Check if event was emitted
			if (transaction.events && transaction.events.ElectionCreated) {
				electionAddress = transaction.events.ElectionCreated.returnValues.electionAddress;
			}

			// Method 2: Try to get the election by email
			if (!electionAddress) {
				try {
					const electionData = await Election_Factory.methods
						.getDeployedElection(email)
						.call({ from: accounts[0] });

					if (electionData && electionData.electionAddress) {
						electionAddress = electionData.electionAddress;
					} else if (Array.isArray(electionData) && electionData.length > 0) {
						electionAddress = electionData[0];
					}
				} catch (lookupError) {
					console.warn("Could not lookup election address:", lookupError);
				}
			}

			if (electionAddress) {
				Cookies.set('address', electionAddress);
				this.setState({
					successMessage: 'Election created successfully! Redirecting...',
					loading: false
				});

				// Redirect after a short delay
				setTimeout(() => {
					Router.pushRoute(`/election/${electionAddress}/company_dashboard`);
				}, 2000);
			} else {
				throw new Error("Election was created but address could not be retrieved");
			}

		} catch (err) {
			console.error("Full error:", err);

			let errorMsg = "Failed to create election. ";

			// Parse different error types
			if (err.message.includes("revert")) {
				errorMsg += "The contract rejected the transaction. This usually means: ";
				errorMsg += "1) You've already created an election, or ";
				errorMsg += "2) The input parameters are invalid.";
			} else if (err.message.includes("gas")) {
				errorMsg += "Insufficient gas. Please try again.";
			} else if (err.message.includes("user denied")) {
				errorMsg += "Transaction was cancelled.";
			} else {
				errorMsg += err.message;
			}

			this.setState({
				errorMess: errorMsg,
				loading: false
			});
		}
	}

	render() {
		const { election_name, election_description, loading, errorMess, successMessage } = this.state;

		return (
			<div style={styles.container}>
				<Helmet>
					<title>Create Election</title>
					<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
					<link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
				</Helmet>

				<div style={styles.background}></div>
				<div style={styles.overlay}></div>

				<Grid textAlign="center" verticalAlign="middle" style={styles.grid}>
					<Grid.Column style={styles.column}>
						<Header as="h2" style={styles.header}>
							Create New Election
						</Header>

						<Segment stacked style={styles.segment}>
							{errorMess && (
								<Message negative style={styles.message}>
									<Icon name="exclamation triangle" />
									{errorMess}
								</Message>
							)}

							{successMessage && (
								<Message positive style={styles.message}>
									<Icon name="check circle" />
									{successMessage}
								</Message>
							)}

							<Form size="large" onSubmit={this.createElection}>
								<Form.Input
									fluid
									icon="address card outline"
									iconPosition="left"
									placeholder="Election Name"
									value={election_name}
									onChange={event => this.setState({ election_name: event.target.value })}
									required={true}
									style={styles.input}
									disabled={loading}
								/>
								<Form.TextArea
									placeholder="Election Description"
									value={election_description}
									onChange={event => this.setState({ election_description: event.target.value })}
									required={true}
									style={styles.textArea}
									disabled={loading}
								/>

								<Button
									color="blue"
									fluid
									size="large"
									loading={loading}
									disabled={loading || !election_name.trim() || !election_description.trim()}
									style={styles.button}
								>
									{loading ? 'Creating Election...' : 'Create Election'}
								</Button>

								<Message icon info style={styles.infoMessage}>
									<Icon name="info circle" />
									<Message.Content>
										<Message.Header>Note:</Message.Header>
										<p>• Election creation may take several minutes</p>
										<p>• Ensure MetaMask is connected</p>
										<p>• You can only create one election per account</p>
									</Message.Content>
								</Message>
							</Form>
						</Segment>
					</Grid.Column>
				</Grid>
			</div>
		);
	}
}

const styles = {
	container: {
		position: 'relative',
		minHeight: '100vh',
		width: '100%',
		overflow: 'hidden'
	},
	background: {
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100vw',
		height: '100vh',
		background: 'url(/static/block.png) no-repeat center center',
		backgroundSize: 'cover',
		zIndex: -2,
		opacity: 0.8
	},
	overlay: {
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100vw',
		height: '100vh',
		backgroundColor: 'rgba(0,0,0,0.6)',
		zIndex: -1
	},
	grid: {
		height: '100vh',
		margin: 0
	},
	column: {
		maxWidth: 500,
		padding: '0 20px'
	},
	header: {
		color: 'white',
		marginBottom: '2rem',
		fontSize: '2.5rem',
		textShadow: '2px 2px 4px rgba(0,0,0,0.8)'
	},
	segment: {
		border: 'none',
		boxShadow: '0 8px 30px rgba(0,0,0,0.4)',
		padding: '2.5rem',
		background: 'rgba(255,255,255,0.97)',
		borderRadius: '12px',
		overflow: 'hidden'
	},
	button: {
		marginTop: '1.5rem',
		borderRadius: '8px',
		fontWeight: 'bold',
		fontSize: '1.1rem',
		padding: '1.2rem',
		backgroundColor: '#273082',
		color: 'white',
		transition: 'all 0.3s ease'
	},
	message: {
		textAlign: 'left',
		marginBottom: '1.5rem'
	},
	input: {
		marginBottom: '1.5rem',
		borderRadius: '6px'
	},
	textArea: {
		marginBottom: '1.5rem',
		minHeight: '120px',
		borderRadius: '6px',
		resize: 'vertical'
	},
	infoMessage: {
		textAlign: 'left',
		marginTop: '1.5rem',
		backgroundColor: '#f8ffff',
		borderRadius: '8px'
	}
};

export default CreateElection;
