import React, { Component } from 'react';
import { Button, Form, Grid, Header, Segment, Icon, Message, Card } from 'semantic-ui-react';
import web3 from '../../Ethereum/web3';
import Election_Factory from '../../Ethereum/election_factory';
import { Router } from '../../routes';
import Cookies from 'js-cookie';
import { Helmet } from 'react-helmet';

class LoginForm extends Component {
	state = {
		election_name: '',
		election_description: '',
		loading: false,
		errorMess: '',
		showHardcodedOption: false,
		hardcodedCreated: false,
		hardcodedElection: null
	};

	componentDidMount() {
		// Check if we're in development mode for hardcoded option
		if (process.env.NODE_ENV === 'development') {
			this.setState({ showHardcodedOption: true });
		}

		// Check if hardcoded election already exists in cookies
		const savedElection = Cookies.get('hardcoded_election');
		if (savedElection) {
			this.setState({
				hardcodedCreated: true,
				hardcodedElection: JSON.parse(savedElection)
			});
		}
	}

	createHardcodedElection = async () => {
		this.setState({ loading: true, errorMess: '' });

		try {
			const accounts = await web3.eth.getAccounts();
			const authority = accounts[0];

			// Create hardcoded election data
			const hardcodedElection = {
				address: '0xH4RDC0D3D3L3CT10N2024', // Mock address
				name: 'Annual Board Election 2024',
				description: 'Hardcoded test election with pre-configured candidates',
				authority: authority,
				candidates: [
					{
						id: 0,
						name: 'John Doe',
						description: 'Experienced leader with 10 years in public service',
						imgHash: 'QmHash123',
						email: 'john@example.com',
						voteCount: 0
					},
					{
						id: 1,
						name: 'Jane Smith',
						description: 'Community advocate and reformist',
						imgHash: 'QmHash456',
						email: 'jane@example.com',
						voteCount: 0
					},
					{
						id: 2,
						name: 'Mike Johnson',
						description: 'Business entrepreneur and innovator',
						imgHash: 'QmHash789',
						email: 'mike@example.com',
						voteCount: 0
					}
				],
				totalVotes: 0,
				status: true,
				createdAt: new Date().toISOString()
			};

			// Save to cookies and state
			Cookies.set('hardcoded_election', JSON.stringify(hardcodedElection));
			Cookies.set('address', hardcodedElection.address);

			this.setState({
				loading: false,
				hardcodedCreated: true,
				hardcodedElection: hardcodedElection,
				errorMess: '✅ Hardcoded election created successfully!'
			});

			// Auto-redirect after 2 seconds
			setTimeout(() => {
				Router.pushRoute(`/election/${hardcodedElection.address}/company_dashboard`);
			}, 2000);

		} catch (err) {
			this.setState({
				loading: false,
				errorMess: 'Error creating hardcoded election: ' + err.message
			});
		}
	};

	signin = async event => {
		event.preventDefault();
		this.setState({ loading: true, errorMess: '' });
		try {
			const email = Cookies.get('company_email');
			const accounts = await web3.eth.getAccounts();

			const bool = await Election_Factory.methods
				.createElection(
					accounts[0], // Use account address instead of email
					this.state.election_name,
					this.state.election_description
				)
				.send({
					from: accounts[0],
					gas: 3000000
				});

			if (bool) {
				const summary = await Election_Factory.methods.getDeployedElection('xyz').call();
				this.setState({ loading: false });
				Cookies.set('address', summary[0]);
				Router.pushRoute(`/election/${summary[0]}/company_dashboard`);
			}
		} catch (err) {
			let errorMsg = err.message;
			if (err.message.includes("Internal JSON-RPC")) {
				errorMsg = "Transaction failed. Try the hardcoded option below for testing.";
			}
			this.setState({
				errorMess: errorMsg,
				loading: false
			});
		}
	};

	renderHardcodedElectionCard() {
		const { hardcodedElection } = this.state;
		if (!hardcodedElection) return null;

		return (
			<Card fluid style={styles.hardcodedCard}>
				<Card.Content>
					<Card.Header>
						<Icon name="star" color="yellow" />
						Hardcoded Election Ready
					</Card.Header>
					<Card.Meta>{hardcodedElection.address}</Card.Meta>
					<Card.Description>
						<strong>{hardcodedElection.name}</strong>
						<p>{hardcodedElection.description}</p>
						<Button
							color="green"
							fluid
							onClick={() => Router.pushRoute(`/election/${hardcodedElection.address}/company_dashboard`)}
						>
							<Icon name="dashboard" />
							Go to Dashboard
						</Button>
					</Card.Description>
				</Card.Content>
			</Card>
		);
	}

	render() {
		const { showHardcodedOption, hardcodedCreated, loading } = this.state;

		return (
			<div style={styles.container}>
				<Helmet>
					<title>Create Election</title>
					<link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
				</Helmet>

				<div style={styles.background}></div>
				<div style={styles.overlay}></div>

				<Grid textAlign="center" verticalAlign="middle" style={styles.grid}>
					<Grid.Column style={styles.column}>
						<Header as="h2" style={styles.header}>
							Create New Election
						</Header>

						{/* Show existing hardcoded election if available */}
						{hardcodedCreated && this.renderHardcodedElectionCard()}

						<Segment stacked style={styles.segment}>
							{this.state.errorMess && (
								<Message
									negative={!this.state.errorMess.includes('✅')}
									positive={this.state.errorMess.includes('✅')}
									style={styles.message}
								>
									{this.state.errorMess}
								</Message>
							)}

							<Form size="large" onSubmit={this.signin}>
								<Form.Input
									fluid
									icon="address card outline"
									iconPosition="left"
									placeholder="Election Name"
									value={this.state.election_name}
									onChange={event => this.setState({ election_name: event.target.value })}
									required={true}
									style={styles.input}
								/>
								<Form.TextArea
									placeholder="Election Description"
									value={this.state.election_description}
									onChange={event => this.setState({ election_description: event.target.value })}
									required={true}
									style={styles.textArea}
								/>

								<Button
									color="blue"
									fluid
									size="large"
									loading={loading}
									disabled={loading}
									style={styles.button}
								>
									Create Real Election
								</Button>

								{showHardcodedOption && (
									<Button
										color="green"
										fluid
										size="large"
										loading={loading}
										disabled={loading}
										onClick={this.createHardcodedElection}
										style={{ ...styles.button, marginTop: '1rem' }}
									>
										<Icon name="magic" />
										Create Hardcoded Election (Test)
									</Button>
								)}

								<Message icon info style={styles.infoMessage}>
									<Icon name="exclamation circle" />
									<Message.Content>
										<Message.Header>Note:</Message.Header>
										{showHardcodedOption
											? "Use 'Hardcoded Election' for instant testing. Real election creation will take several minutes."
											: "Election creation will take several minutes."
										}
									</Message.Content>
								</Message>
							</Form>
						</Segment>

						{/* Hardcoded Election Preview */}
						{showHardcodedOption && !hardcodedCreated && (
							<Message style={styles.previewMessage}>
								<Header as="h4">Hardcoded Election will include:</Header>
								<ul>
									<li>3 pre-configured candidates</li>
									<li>Instant deployment</li>
									<li>Test voting functionality</li>
									<li>Full dashboard access</li>
								</ul>
							</Message>
						)}
					</Grid.Column>
				</Grid>
			</div>
		);
	}
}

const styles = {
	container: {
		position: 'relative',
		minHeight: '100vh',
		width: '100%',
		overflow: 'hidden'
	},
	background: {
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100vw',
		height: '100vh',
		background: 'url(/static/block.png) no-repeat center center',
		backgroundSize: 'cover',
		zIndex: -2
	},
	overlay: {
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100vw',
		height: '100vh',
		backgroundColor: 'rgba(0,0,0,0.5)',
		zIndex: -1
	},
	grid: {
		height: '100vh',
		margin: 0
	},
	column: {
		maxWidth: 550,
		padding: '0 20px'
	},
	header: {
		color: 'white',
		marginBottom: '2rem',
		fontSize: '2.5rem',
		textShadow: '2px 2px 4px rgba(0,0,0,0.8)'
	},
	segment: {
		border: 'none',
		boxShadow: '0 5px 25px rgba(0,0,0,0.3)',
		padding: '2rem',
		background: 'rgba(255,255,255,0.95)',
		borderRadius: '10px',
		overflow: 'hidden'
	},
	button: {
		marginTop: '1rem',
		borderRadius: '6px',
		fontWeight: 'bold',
		fontSize: '1.1rem',
		padding: '1rem',
		backgroundColor: '#273082',
		color: 'white'
	},
	message: {
		textAlign: 'left',
		marginBottom: '1.5rem'
	},
	input: {
		marginBottom: '1.5rem'
	},
	textArea: {
		marginBottom: '1.5rem',
		minHeight: '100px'
	},
	infoMessage: {
		textAlign: 'left',
		marginTop: '1.5rem'
	},
	hardcodedCard: {
		marginBottom: '2rem',
		background: 'linear-gradient(135deg, #e3f2fd 0%, #bbdefb 100%)',
		border: '2px solid #2196f3'
	},
	previewMessage: {
		marginTop: '2rem',
		background: 'rgba(255,255,255,0.9)'
	}
};

export default LoginForm;
