                    Candidate List
                  </Header>
                  <Container>
                    <table>
                      {this.renderTable()}
                    </table>
                  </Container>
                </Grid.Column>
                <Grid.Column style={{ float: 'right', width: '30%' }}>
                  <Container style={{ marginLeft: '50px' }}>
