import React, { Component } from 'react';
import { Grid, Button, Header, Icon, Image, Menu, Sidebar, Container, Card, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import web3 from '../../Ethereum/web3';
import Election from '../../Ethereum/election';
import Cookies from 'js-cookie';
import { Router } from '../../routes';
import { Helmet } from 'react-helmet';

class VotingList extends Component {
  state = {
    election_address: Cookies.get('address'),
    election_name: '',
    election_description: '',
    candidates: [],
    items: [],
    loading: true,
    error: null,
    hasVoted: false // Track if the voter has already voted
  };

  componentDidMount() {
    this.initializeElection();
  }

  initializeElection = async () => {
    try {
      const add = Cookies.get('address');
      console.log("Election Address from Cookie:", add);

      if (!add) {
        throw new Error('Election address not found in cookies. Please login again.');
      }

      const voterEmail = Cookies.get('voter_email');
      if (!voterEmail) {
        throw new Error('Voter email not found in cookies. Please login again.');
      }

      const election = Election(add);
      console.log("Election instance created successfully");

      // Get election details
      const summary = await election.methods.getElectionDetails().call();
      this.setState({
        election_name: summary[0],
        election_description: summary[1]
      });

      // Check if voter has already voted
      const hasVoted = await election.methods.hasVoted(voterEmail).call();
      this.setState({ hasVoted });

      // Get number of candidates
      const c = await election.methods.getNumOfCandidates().call();
      console.log("Number of candidates:", c);

      let candidates = [];
      for (let i = 0; i < c; i++) {
        const candidate = await election.methods.getCandidate(i).call();
        candidates.push({
          id: i,
          name: candidate[0],
          position: candidate[1], // Assuming this is the position
          bio: candidate[2], // Assuming this is the bio
          imageHash: candidate[3], // Assuming this is the IPFS hash
          votes: candidate[4] // Assuming this is the vote count
        });
      }

      const items = candidates.map(candidate => {
        return {
          header: candidate.name,
          meta: candidate.position,
          description: candidate.bio,
          image: (
            <Image
              src={`https://ipfs.io/ipfs/${candidate.imageHash}`}
              style={{ maxWidth: '100%', maxHeight: '190px' }}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/300x200?text=Candidate+Image';
              }}
            />
          ),
          extra: (
            <div>
              <Icon name='pie graph' size='big' />
              {candidate.votes.toString()} Votes
              <Button
                id={candidate.id}
                style={{ float: 'right' }}
                onClick={this.vote}
                primary
                disabled={this.state.hasVoted}
              >
                {this.state.hasVoted ? 'Already Voted' : 'Vote!'}
              </Button>
            </div>
          )
        };
      });

      this.setState({
        items: items,
        candidates,
        loading: false,
        error: null
      });
    } catch (err) {
      console.error("Error in initializeElection:", err.message);
      this.setState({
        error: err.message,
        loading: false
      });

      if (err.message.includes('not found') || err.message.includes('login')) {
        alert("Session expired. Redirecting you to login page...");
        Router.pushRoute('/voter_login');
      }
    }
  }

  SidebarExampleVisible = () => (
    <Sidebar.Pushable>
      <Sidebar as={Menu} animation='overlay' icon='labeled' inverted vertical visible width='thin' style={{ backgroundColor: 'white', borderWidth: "10px" }}>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <h2>MENU</h2><hr />
        </Menu.Item>
        <Menu.Item as='a' style={{ color: 'grey' }} >
          <Icon name='dashboard' />
          Dashboard
        </Menu.Item>
        <hr />
        <Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
          <Menu.Item as='a' style={{ color: 'grey' }}>
            <Icon name='sign out' />
            Sign Out
          </Menu.Item>
        </Button>
      </Sidebar>
    </Sidebar.Pushable>
  )

  signOut = () => {
    Cookies.remove('address');
    Cookies.remove('voter_email');
    alert("Logging out.");
    Router.pushRoute('/homepage');
  }

  getElectionDetails = () => {
    const { election_name, election_description, hasVoted } = this.state;

    return (
      <div style={{ textAlign: 'center', marginBottom: '2%', marginTop: '2%' }}>
        <Header as="h2">
          <Icon name="address card" />
          <Header.Content>
            {election_name}
            <Header.Subheader>{election_description}</Header.Subheader>
            {hasVoted && (
              <Message positive style={{ marginTop: '1rem' }}>
                <Icon name="check circle" />
                You have already voted in this election.
              </Message>
            )}
          </Header.Content>
        </Header>
      </div>
    );
  }

  renderTable = () => {
    const { items, loading, error } = this.state;

    if (error) {
      return (
        <Message negative>
          <Message.Header>Error</Message.Header>
          <p>{error}</p>
          <Button primary onClick={this.initializeElection}>Retry</Button>
          <Button secondary onClick={() => Router.pushRoute('/voter_login')}>Login Again</Button>
        </Message>
      );
    }

    if (loading) {
      return (
        <Message info>
          <Icon name="circle notched" loading />
          Loading candidates...
        </Message>
      );
    }

    if (items.length === 0) {
      return (
        <Message warning>
          <Icon name="warning circle" />
          No candidates available for this election.
        </Message>
      );
    }

    return <Card.Group items={items} itemsPerRow={2} />;
  }

  vote = async event => {
    try {
      const candidateId = parseInt(event.currentTarget.id, 10);
      const accounts = await web3.eth.getAccounts();
      const add = Cookies.get('address');
      const voterEmail = Cookies.get('voter_email');

      if (!add || !voterEmail) {
        throw new Error('Session expired. Please login again.');
      }

      this.setState({ loading: true });

      const election = Election(add);

      // Call the vote method
      await election.methods.vote(candidateId, voterEmail).send({ from: accounts[0] });

      alert("Voted successfully!");

      // Refresh the candidate list to update vote counts
      await this.initializeElection();
    } catch (err) {
      console.error("Error voting:", err.message);
      this.setState({ loading: false });
      alert(`Voting failed: ${err.message}`);
    }
  }

  render() {
    return (
      <div>
        <Helmet>
          <title>Vote</title>
          <link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
        </Helmet>
        <Grid>
          <Grid.Row>
            <Grid.Column width={2}>
              {this.SidebarExampleVisible()}
            </Grid.Column>
            <Grid.Column width={14}>
              <Layout>
                {this.getElectionDetails()}
                <Container>
                  {this.renderTable()}
                </Container>
              </Layout>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default VotingList;
