// components/CompanyDashboard.js
import React, { Component } from 'react';
import { Grid, Step, Icon, Menu, Sidebar, Header, Button, Message } from 'semantic-ui-react';
import Layout from '../../components/Layout';
import { Bar, Pie } from 'react-chartjs-2';
import Cookies from 'js-cookie';
import { Link, Router } from '../../routes';
import { Helmet } from 'react-helmet';
import SAMPLE_ELECTION from '../sampleElectionData';

class CompanyDashboard extends Component {
	state = {
		election_address: SAMPLE_ELECTION.address,
		election_name: SAMPLE_ELECTION.name,
		election_desc: SAMPLE_ELECTION.description,
		voters: SAMPLE_ELECTION.voters,
		candidates: SAMPLE_ELECTION.candidates,
		votes: SAMPLE_ELECTION.totalVotes,
		loading: false,
		electionStatus: SAMPLE_ELECTION.electionStatus,
		chartData: {
			labels: SAMPLE_ELECTION.candidateData.map(candidate => candidate.name),
			datasets: [{
				label: 'Vote Counts',
				backgroundColor: [
					'rgba(255, 99, 132, 0.6)',
					'rgba(54, 162, 235, 0.6)',
					'rgba(255, 206, 86, 0.6)',
					'rgba(75, 192, 192, 0.6)',
					'rgba(153, 102, 255, 0.6)'
				],
				borderColor: [
					'rgba(255, 99, 132, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)'
				],
				borderWidth: 2,
				hoverBackgroundColor: [
					'rgba(255, 99, 132, 0.8)',
					'rgba(54, 162, 235, 0.8)',
					'rgba(255, 206, 86, 0.8)',
					'rgba(75, 192, 192, 0.8)',
					'rgba(153, 102, 255, 0.8)'
				],
				hoverBorderColor: [
					'rgba(255, 99, 132, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)'
				],
				data: SAMPLE_ELECTION.candidateData.map(candidate => candidate.votes),
			}]
		}
	};

	componentDidMount() {
		// Set the election address in cookies for other components
		Cookies.set('address', SAMPLE_ELECTION.address);
		Cookies.set('election_name', SAMPLE_ELECTION.name);
	}

	getElectionDetails = () => {
		const { election_name, election_desc, electionStatus } = this.state;

		return (
			<div style={{ marginBottom: '2%', marginTop: '2%' }}>
				<Header as="h2">
					<Icon name="address card" />
					<Header.Content>
						{election_name}
						<Header.Subheader>
							{election_desc}
							<div style={{ marginTop: '5px', fontSize: '0.8em', color: electionStatus === 'active' ? 'green' : 'gray' }}>
								Status: {electionStatus.toUpperCase()}
							</div>
						</Header.Subheader>
					</Header.Content>
				</Header>
			</div>
		);
	};

	SidebarExampleVisible = () => (
		<Sidebar.Pushable>
			<Sidebar
				as={Menu}
				animation="overlay"
				icon="labeled"
				inverted
				vertical
				visible
				width="thin"
				style={{ backgroundColor: 'white', borderWidth: '10px' }}
			>
				<Menu.Item as="a" style={{ color: 'grey' }}>
					<h2>MENU</h2>
					<hr />
				</Menu.Item>
				<Link route={`/election/${this.state.election_address}/company_dashboard`}>
					<a>
						<Menu.Item style={{ color: 'grey', fontColor: 'grey' }}>
							<Icon name="dashboard" />
							Dashboard
						</Menu.Item>
					</a>
				</Link>
				<Link route={`/election/${this.state.election_address}/candidate_list`}>
					<a>
						<Menu.Item as="a" style={{ color: 'grey' }}>
							<Icon name="user outline" />
							Candidate List
						</Menu.Item>
					</a>
				</Link>
				<Link route={`/election/${this.state.election_address}/voting_list`}>
					<a>
						<Menu.Item as="a" style={{ color: 'grey' }}>
							<Icon name="list" />
							Voter List
						</Menu.Item>
					</a>
				</Link>
				<hr />
				<Button onClick={this.signOut} style={{ backgroundColor: 'white' }}>
					<Menu.Item as="a" style={{ color: 'grey' }}>
						<Icon name="sign out" />
						Sign Out
					</Menu.Item>
				</Button>
			</Sidebar>
		</Sidebar.Pushable>
	);

	signOut = () => {
		Cookies.remove('address');
		Cookies.remove('company_email');
		Cookies.remove('company_id');
		alert('Logging out.');
		Router.pushRoute('/homepage');
	};

	endElection = () => {
		this.setState({ loading: true });

		// Simulate API call to end election
		setTimeout(() => {
			this.setState({
				loading: false,
				electionStatus: 'completed',
			});
			alert('Election has been ended successfully! Results have been sent to all participants.');
		}, 1500);
	};

	render() {
		const chartOptions = {
			maintainAspectRatio: true,
			responsive: true,
			scales: {
				yAxes: [{
					ticks: {
						beginAtZero: true,
						stepSize: 10
					}
				}]
			},
			legend: {
				display: true,
				position: 'bottom'
			}
		};

		const pieOptions = {
			maintainAspectRatio: true,
			responsive: true,
			legend: {
				display: true,
				position: 'bottom'
			}
		};

		return (
			<div>
				<Helmet>
					<title>Dashboard - {this.state.election_name}</title>
					<link rel="shortcut icon" type="image/x-icon" href="../../static/logo-Block.png" />
				</Helmet>

				<Grid>
					<Grid.Row>
						<Grid.Column width={2}>
							{this.SidebarExampleVisible()}
						</Grid.Column>

						<Grid.Column width={14}>
							<Layout>
								<Message info>
									<Icon name="info circle" />
									This is a demonstration dashboard using sample election data.
								</Message>

								{this.getElectionDetails()}

								{this.state.electionStatus === 'active' && (
									<Button
										negative
										style={{ float: 'right', marginTop: '2%' }}
										onClick={this.endElection}
										loading={this.state.loading}
									>
										End Election
									</Button>
								)}

								<Step.Group style={{ minWidth: '100%', minHeight: 90 }}>
									<Step icon="users" title="Total Voters" description={this.state.voters} />
									<Step icon="user outline" title="Candidates" description={this.state.candidates} />
									<Step icon="check square" title="Votes Cast" description={this.state.votes} />
									<Step
										icon="percent"
										title="Participation Rate"
										description={`${Math.round((this.state.votes / this.state.voters) * 100)}%`}
									/>
								</Step.Group>

								<Grid.Row>
									<Grid.Column width={8}>
										<Header as="h3" textAlign="center">
											<Icon name="bar chart" />
											Vote Distribution
										</Header>
										<div style={{ height: '400px', padding: '20px' }}>
											<Bar
												data={this.state.chartData}
												options={chartOptions}
												height={100}
											/>
										</div>
									</Grid.Column>

									<Grid.Column width={8}>
										<Header as="h3" textAlign="center">
											<Icon name="pie chart" />
											Results Overview
										</Header>
										<div style={{ height: '400px', padding: '20px' }}>
											<Pie
												data={this.state.chartData}
												options={pieOptions}
												height={100}
											/>
										</div>
									</Grid.Column>
								</Grid.Row>

								<Grid.Row>
									<Grid.Column width={16}>
										<Header as="h3">
											<Icon name="trophy" />
											Current Leader
										</Header>
										<Message positive>
											<Message.Header>
												{SAMPLE_ELECTION.candidateData[0].name} - {SAMPLE_ELECTION.candidateData[0].votes} votes
											</Message.Header>
											<p>{SAMPLE_ELECTION.candidateData[0].position}</p>
										</Message>
									</Grid.Column>
								</Grid.Row>
							</Layout>
						</Grid.Column>
					</Grid.Row>
				</Grid>
			</div>
		);
	}
}

export default CompanyDashboard;
