import web3 from './web3';
import ElectionFact from './Build/ElectionFact.json';

// DEBUG: Check what's actually imported
console.log('Full ElectionFact import:', ElectionFact);
console.log('ABI path:', ElectionFact.abi);

// SAFE EXTRACTION (compatible syntax)
const contractABI = ElectionFact.abi ||
    (ElectionFact.compilerOutput && ElectionFact.compilerOutput.abi) ||
    (ElectionFact.metadata && JSON.parse(ElectionFact.metadata).output.abi);

if (!contractABI) throw new Error("No ABI found in JSON file");

const instance = new web3.eth.Contract(
    contractABI,
    '0x16795911522115Ef7D4F099f1aBe1F76C2dB6AC8' // Your address
);

export default instance;
