import React from 'react';

const BackgroundImage = () => {
	return (
		<div style={{
			backgroundImage: "url('/static/block.png')",
			backgroundSize: 'cover',
			backgroundPosition: 'center',
			backgroundRepeat: 'no-repeat',
			width: '100vw',
			height: '100vh',
			position: 'fixed',  // This makes it stay in place during scrolling
			top: 0,
			left: 0,
			margin: 0,
			padding: 0,
			zIndex: -1,  // Ensures it stays behind other content
		}}>
			{/* Content Container */}
			<div style={{
				position: 'relative',
				width: '100vw',
				height: '100vh',
				display: 'flex',
				flexDirection: 'column',
				justifyContent: 'center',
				alignItems: 'center',
				color: 'white',
				textAlign: 'center'
			}}>
				{/* Main Heading */}
				<h1 style={{
					fontSize: '4rem',
					textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)',
					marginBottom: '1rem',
					fontWeight: 'bold',
					backgroundColor: 'rgba(0, 0, 0, 0.5)',
					padding: '20px 40px',
					borderRadius: '10px'
				}}>
					BLOCKCHAIN VOTING SYSTEM
				</h1>

				{/* Sub Content */}
				<div style={{
					maxWidth: '800px',
					fontSize: '1.5rem',
					lineHeight: '1.6',
					textShadow: '1px 1px 2px rgba(0, 0, 0, 0.8)',
					backgroundColor: 'rgba(0, 0, 0, 0.4)',
					padding: '2rem',
					borderRadius: '10px',
					margin: '0 2rem'
				}}>
					<p>A secure, transparent and tamper-proof voting solution powered by blockchain technology.</p>
					<p style={{ marginTop: '1rem', fontStyle: 'italic' }}>
						Every vote counts. Every vote is verifiable. Every vote is immutable.
					</p>
				</div>
			</div>
		</div>
	);
};

export default BackgroundImage;
